func (c *Client) GetPokemonByName(pokemonName string) (Pokemon, error) {

	url := URL + "/pokemon/" + pokemonName

	// if url with pokemonName is in the cache then ok will be true
	if val, ok := c.cache.GetEntry(url); ok {
		// creating empty pokemon variable
		pokemon := Pokemon{}
		// unmarshal var into pokemon variable
		err := json.Unmarshal(val, &pokemon)
		if err != nil {
			return Pokemon{}, err
		}
		return pokemon, nil
	}

	// ================= USING THE POKEAPI ================
	// Fetching data (if not cached)
	// creating new GET Request on url and error handling accordingly
	req, err := http.NewRequest("GET", url, nil)
	if err != nil {
		return Pokemon{}, err
	}
	// stores the Response in res
	res, err := c.httpClient.Do(req)
	if err != nil {
		return Pokemon{}, err
	}

	// Ensure that response body is closed
	defer res.Body.Close()
	// ReadAll function reads response body into data byte slice
	data, err := io.ReadAll(res.Body)
	if err != nil {
		return Pokemon{}, err
	}

	// Creating empty Pokemon named pokemon
	pokemon := Pokemon{}
	// Storing data from response body in pokemon
	err = json.Unmarshal(data, &pokemon)
	if err != nil {
		return Pokemon{}, err
	}
	// storing data in client's cache. This step caches the data for future use
	c.cache.AddEntry(url, data)
	return pokemon, nil
}
